mapper.insert:
insert 
  into Class	
	 (
		c_no
	  , mem_no
	  , c_title
	  , c_content
	  , c_location
	  , c_date
	  , c_time
	  , c_price
	  , c_approval
	  , minage
	  , maxage
	  , people_limit
	  , cl_type
	  , cl_category
	  , curriculum
	  , latitude
	  , longitude
  	  , enrollDate
  	  , placeId
	 )
values
	 (
	    'C'||seq_cno.nextval
	  , #{memNo}
	  , #{classTitle}
	  , #{classContent}
	  , #{classLocation}
	  , #{classDate}
	  , #{classTime}
	  , #{classPrice}
	  , #{classApproval}
	  , #{minAge}
	  , #{maxAge}
	  , #{peopleLimit}
	  , #{clType}
	  , #{clCategory}
	  , #{curriculum}
	  , #{latitude}
	  , #{longitude}
	  , sysdate
	  , #{placeId}
	 )

mapper.select:
select 
       count(*)
  from my_class
 where c_no = #{classNo}
   and mem_no = #{memNo}

select 
       cl_type_status
  from my_class
 where c_no = #{classNo}
   and mem_no = #{memNo}

mapper.insert:
insert
  into my_class
  (
  	c_no
  ,	mem_no
  , cl_type_status
  )
  values
  (
  	#{classNo}
  ,	#{memNo}
  , #{clType}
  )

mapper.delete:
update my_class
   set cl_type_status = 6
 where mem_no = #{memNo}
   and c_no = #{classNo}

mapper.insert:
insert
  into favorites
  (
  	c_no
  , mem_no	
  )
values
  (
  	#{classNo}
  , #{memNo}
  )

mapper.delete:
delete
  from favorites
 where c_no = #{classNo}
   and mem_no = #{memNo}

mapper.select:
select
	   count(*)
  from favorites
 where c_no = #{classNo}
   and mem_no = #{memNo}

mapper.update:
update class
   set status ='N'
 where c_no = #{classNo}

mapper.select:
select c_price
  from class c
  join payment p on (c.c_no = p.c_no)
 where p.c_no = #{classNo}
   and p.mem_no = #{memNo}

mapper.insert:
insert
  into quitReason
	  (
	  	c_no
	  , mem_no
	  , quitReason
	  , detailReason	
	  )
  values
	  (
	  	#{classNo}
	  , #{memNo}	
	  , #{quitReason}
	  , #{detailReason}
	  )

mapper.update:
update my_class
   set cl_type_status = 4
 where mem_no = #{memNo}
   and c_no = #{classNo}

update my_class
   set cl_type_status = 5
 where mem_no = #{memNo}
   and c_no = #{classNo}

mapper.select:
select c_no , mem_no , c_title , c_content , c_location , c_date , c_time , c_price , c_approval , minage , maxage , people_limit , cl_type , cl_category , curriculum , origin_name , update_name , file_path , cl_name , cl_ca_name , (select count(*) from my_class where cl_type_status = 1 and my_class.c_no = class.c_no ) vacancy from class left join attachment on (c_no = ref_fno) join cl_category on (cl_category = cl_categoryno) join cl_type on (cl_type = cl_ca_no) where status = 'Y' and cl_type = #{clType}
and cl_category = #{clCategory}
group by c_no , mem_no , c_title , c_content , c_location , c_date , c_time , c_price , c_approval , minage , maxage , people_limit , cl_type , cl_category , curriculum , origin_name , update_name , file_path , cl_name , cl_ca_name , enrolldate order by enrolldate desc
